import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import React from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick-theme.css";
import "slick-carousel/slick/slick.css";
import carousel1 from '../../../Assets/Images/carousel1.jpg';
import carousel2 from '../../../Assets/Images/carousel2.jpg';
import carousel3 from '../../../Assets/Images/carousel3.jpg';
import playIcon from '../../../Assets/Images/playIcon.png';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 800,
    height: 500,
    bgcolor: 'background.paper',
    boxShadow: 24,
};
function Carousel() {

    // const bannerList = useSelector(state => state.MovieManagerReducer.bannerList)
    const bannerList = [
        {
            hinhAnh: carousel1
        },
        {
            hinhAnh: carousel2
        },
        {
            hinhAnh: carousel3
        }
    ]
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,

    };
    return (
        <div className='carousel'>
            <Slider {...settings}>
                {bannerList.map((banner, index) => {
                    return <div key={index} className='carousel-item'>
                        <img className='carousel-background' src={banner.hinhAnh} alt='...' />
                        <Button className='carousel-play' onClick={handleOpen}><img src={playIcon} style={{ width: '100%', }} alt='...' /></Button>
                    </div>
                })}
            </Slider>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <iframe title='trailer' width="100%" height="100%" src="https://www.youtube.com/embed/OB3g37GTALc">
                    </iframe>
                </Box>
            </Modal>
        </div>
    )
}
export default React.memo(Carousel)
